mapper.update:
update sky_take_out.shopping_cart set
number= number+1
where

    dish_id=#{dishId}


    setmeal_id=#{setmealId}

and user_id=#{userId}

update sky_take_out.shopping_cart
set number=number - 1


        dish_id=#{dishId}


        setmeal_id=#{setmealId}


        and dish_flavor=#{dishFlavor}

    and user_id=#{userId}

mapper.select:
select * from sky_take_out.shopping_cart


        dish_id=#{dishId}


        setmeal_id=#{setmealId}


        and dish_flavor=#{dishFlavor}

    and user_id=#{userId}
